import React, { Component } from 'react';
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageInputLinkForm from './components/ImageInputLinkForm/ImageInputLinkForm';
import './App.css';
import Rank from './components/Rank/Rank';
import Particles from 'react-particles-js';
import particleconfig from './particleconfig';
import Clarifai from 'clarifai';

const app = new Clarifai.App({
 apiKey: 'bc16c17efb7149c6be47b7366fdd88b7'
});

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box:'{}',
      route:'signin',
      isSignedIn: false
    }
  }

    calculateFaceLocation = (data) => {
      const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
      const image = document.getElementById('inputimage');
      const width = Number(image.width);
      const height = Number(image.height);
      return {
        leftCol: clarifaiFace.left_col * width, //margin from left of image
        topRow: clarifaiFace.top_row * height,//margin from top of image
        rightCol: width - (clarifaiFace.right_col * width),//margin from right of image
        bottomRow: height - (clarifaiFace.bottom_row * height)//margin from bottom of image

      }
    }

    displayFaceBox = (box) => { //box will be a border relative to the image based on the position given
      this.setState({box:box});
    }

    onInputChange = (event) => {
      this.setState({input: event.target.value});
    }

    onButtonSubmit = () => {
      this.setState({imageUrl: this.state.input});
    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
      .then(response => this.displayFaceBox(this.calculateFaceLocation(response)))
      .catch(err =>console.log(err));
    }
    onRouteChange = (route) => 
    {
        if (route === 'signout'){ //navigation bar
          this.setState({isSignedIn: false});
        } else if (route === 'home'){
          this.setState({isSignedIn: true});
        } 
      this.setState({route: route});

    }

      render() {
        return (
     <div className="App">
        <Particles className='particles' params={particleconfig}/>
        <Navigation onRouteChange={this.onRouteChange} isSignedIn={this.state.isSignedIn} />
          {this.state.route === 'home' 
          ?//Home          
            <div>
              <Logo />
              <Rank/>
              <ImageInputLinkForm 
               onInputChange={this.onInputChange} 
               onButtonSubmit={this.onButtonSubmit}/>
               <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl} />
               </div>
          ://Signin + Register
         (
            this.state.route === 'signin'
            ? //Signin
              <Signin onRouteChange={this.onRouteChange} />
            : //Register
             <Register onRouteChange={this.onRouteChange} />
            )
           }
          </div>
        );

      }
    }

    export default App;
