import React from 'react';



class Register extends React.Component {
 constructor(props) { // to use props from App.js we need to pass it
    super();
    this.state = {
      email: '',
      password: '',
      name: ''
    }
  }

  onNameChange = (event) => {
    this.setState({name: event.target.value})
  }

  onEmailChange = (event) => {
    this.setState({email: event.target.value})
  }

  onPasswordChange = (event) => {
    this.setState ({password: event.target.value})
  }


  render(){
return (
	<article className="w-20 center bg-white br3 pa3 pa4-ns mv ba b--black-10 shadow-5">
    	<main className="pa4 black-80">
      <div className="measure">
        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
          <legend className="f1 fw6 ph0 mh0">Register</legend>
            <div className="mt3">
            <label className="tl db fw6 lh-copy f6" htmlFor="email-address">Name</label>
            <input className="pa2 input-reset ba bg-transparent w-100" type="text" name="name"  id="name"/>
          </div>
          <div className="mt3">
            <label className="tl db fw6 lh-copy f6" htmlFor="email-address">Email</label>
            <input className="pa2 input-reset ba bg-transparent w-100" type="email" name="email-address"  id="email-address"/>
          </div>
          <div className="mv3">
            <label className="tl db fw6 lh-copy f6" htmlFor="password">Password</label>
            <input className="b pa2 input-reset ba bg-transparent w-100" type="password" name="password"  id="password"/>
          </div>
        </fieldset>
        <div className="">
          <input 
          onClick ={() => onRouteChange('home')}
          className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f4 dib" 
          type="submit" 
          value="Register"/>
        </div>
      </div>
    </main>
  </article>
);
  }


}

export default Register;
