import React, { Component } from 'react';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageInputLinkForm from './components/ImageInputLinkForm/ImageInputLinkForm';
import './App.css';
import Rank from './components/Rank/Rank';
import Particles from 'react-particles-js';
import particleconfig from './particleconfig';
import Clarifai from 'clarifai';

const app = new Clarifai.App({
 apiKey: 'bc16c17efb7149c6be47b7366fdd88b7'
});

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box:''
    }
  }

calculateFaceLocation = (data) => {
  const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
  const image = document.getElementById('inputimage');
  const width = Number(image.width);
  const height = Number(image.height);
  return {
    leftCol: clarifaiFace.left_col * width, //margin width
    topRow: clarifaiFace.top_row * height,//margin height
    rightCol: width - (clarifaiFace.right_col * width),//width of detection box
    bottomRow: height - (clarifaiFace.bottom_row * height)//height of detection box

  }
}

displayFaceBox = (box) => {
  console.log(box);
  this.setState({box:box});
}

onInputChange = (event) => {
  this.setState({input: event.target.value});
}

onButtonSubmit = () => {
  this.setState({imageUrl: this.state.input});
app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
  .then(response => this.displayFaceBox(this.calculateFaceLocation(response)))
  .catch(err =>console.log(err));
}

  render() {
    return (
      <div className="App">
      <Particles className='particles' params={particleconfig}/>
      <Navigation />
        <Logo />
        <Rank/>
       <ImageInputLinkForm 
       onInputChange={this.onInputChange} 
       onButtonSubmit={this.onButtonSubmit}/>
       <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl} />

      </div>
    );

  }
}

export default App;
